import './Navbar.css'

import React, { useState } from "react";
import "./App.css";

function Navbar() {
  // O estado do tema: "light" ou "dark"
  const [theme, setTheme] = useState("light");

  // Função para trocar o tema
  const toggleTheme = (event) => {
    setTheme(event.target.value);
  };
  function Menu() {
    // Lógica do componente Navbar
    const [isOpen, setIsOpen] = useState(false);
  
    const toggleMenu = () => {
      setIsOpen(!isOpen);
    };
  }

  // Adicionando uma classe ao body para controlar os estilos com base no tema
  React.useEffect(() => {
    document.body.className = theme; // Define a classe 'light' ou 'dark' no body
  }, [theme]);

  // Estado para controlar se o menu está aberto ou fechado
  const [isOpen, setIsOpen] = useState(false);

  // Função para alternar a visibilidade do menu
  const toggleMenu = () => {
    setIsOpen(!isOpen);
  }

  return (
    <div className='navbar'>
      
      {/* Barra de navegação */}
      <nav className="navbar">
        {/* Botão do menu hambúrguer */}
        <div className={`hamburger ${isOpen ? "open" : ""}`} onClick={toggleMenu}>
          <div className="line"></div>
          <div className="line"></div>
          <div className="line"></div>
        </div>

        {/* Menu de navegação */}
        <div className={`menu ${isOpen ? "open" : ""}`}>
          <ul>
            <li>
              <a href="#Sobre" onClick={() => setIsOpen(false)}>Sobre</a>
            </li>
            <li>
              <a href="#Habilidades" onClick={() => setIsOpen(false)}>Habilidades</a>
            </li>
            <li>
              <a href="#services" onClick={() => setIsOpen(false)}>Serviços</a>
            </li>
            <li>
              <a href="#contact" onClick={() => setIsOpen(false)}>Contato</a>
            </li>
          </ul>
        </div>
      </nav>
      
        <h1>Portfólio</h1>

        <div className="theme-toggle">
        <input
          type="radio"
          id="light"
          name="theme"
          value="light"
          checked={theme === "light"}
          onChange={toggleTheme}
        />
        <label htmlFor="light" className="radio-btn">
          Claro
        </label>

        <input
          type="radio"
          id="dark"
          name="theme"
          value="dark"
          checked={theme === "dark"}
          onChange={toggleTheme}
        />
        <label htmlFor="dark" className="radio-btn">
          Escuro
        </label>
      </div>

    </div>
  );
}

export default Navbar;
